const Palabras = (props) => {
    return (
      <div>
        <Foto username={props.username} picture={props.image} />
      </div>
    );
  };
  const imgStyle = {
    border: "5px solid red",
    fontWeigth: "bold",
    width: "200px",
    mraginBottom: "10px"
  }
  const Foto = (props) => {
    const imgSrc = `${logo}?text=${props.username}`;
    return <img src={imgSrc} alt ={props.username} />;
  };

  export default Palabras;
