//import logo from './logo.svg';
import {useState} from 'react';
import './App.css';
const source = 'https://palabras-aleatorias-public-api.herokuapp.com/random'
export default function Home(){
  //const [palabras, setPalabras] = useState([]);
  let palabra;
  const palabras = fetch(source).then(response =>{
    if(response.status === 200){
      console.log("everything went well");
      response.json().then(data => {
        console.log(data);
        palabra = data.map(p =>{
          console.debug(palabra)
        });
      });
    }
    console.log(response);
  }).catch(error => {
    console.error(error);
  });
  if (palabra === null){
    return <div>No data have been received</div>
  }
  const readWords = () =>{
    console.debug("descargando palabra aleatoria...");
  }
  const registros = [
      {
          charadaWord: 'Perro'
      },
      {
          charadaWord: 'Gato'
      },
      {
          charadaWord: 'Perico'
      },
      {
          charadaWord: 'Tortuga'
      },
      {
          charadaWord: 'Aguila'
      }
  ]

  return(

      <div>
        <div>
          <button className = "button is-primary" onclick = {readWords()} >nueva palabra</button>
        </div>
        <div>
          {
              registros.map(contacto =>{
                  return(
                      <div>
                          <strong>{contacto.charadaWord}</strong>
                      </div>
                  )
              }

              )
          }
        </div>
        <div>omar 
      </div>
      </div>

  )

  }
