const Gamer = (props) =>{
    return (
    <div>
        <div>
            <label>{props.caption}</label>
        </div>
        <div>
            <Texto longitud={props.longitud} word={props.word} ></Texto>
        </div>
        <div>
            <Boton des_boton={props .des_boton}></Boton>
        </div>
    </div>
    )
}

const Texto = (props) =>{

    return (<input type="text" id="letra_a_evaluar" word={props.word}/>

    )
}
const Etiqueta = (props) =>{

    return (<label>{props.caption}
        </label>
    )
}
function validaLetra(){
    let x = document.getElementById("letra_a_evaluar").value;
    alert(document.getElementById("letra_a_evaluar").word);
    console.log(x);
    if ("omar".indexOf(x)>-1){
        alert("existe");
    }
}
const Boton = (props) => {
    return (<button  onClick={validaLetra}   >{props.des_boton}
        </button>
    )
}
export default Gamer;
