import base from "../img/1.jpg";
/*import cabeza from "../img/2.jpg";
import mano_i from "../img/3.jpg";
import mano_d from "../img/4.jpg";
import pierna_i from "../img/5.jpg";
import pierna_d from "../img/6.jpg";*/

const Palabras = (props) => {
    return (
      <div>
        <div>
          <Foto word={props.word} picture={props.image} />
        </div>
        <div>
          {props.word}
          {props.longitud}
        </div>
        <div>
          <Espacios longitud={props.longitud}/>
        </div>
      </div>
    );
  };
  const Espacios = (props) =>{
    let espacios = "";
    console.log("espacios  ", props.longitud);
    var ind = 0;
    for (ind in props.longitud){
      espacios = espacios + " __";
      console.log("espacios  ", espacios);
    }
    return <div>{espacios}</div>
  };
  const imgStyle = {
    border: "1px solid red",
    fontWeigth: "bold",
    width: "280px",
    mraginBottom: "10px"
  }
  const Foto = (props) => {
    const imgSrc = `${base}`;
    //console.log(imgSrc);
    return <img src={imgSrc} alt ={props.word} />;
  };

  export default Palabras;
