import pierna_d from "../img/6.jpg";*/
const Gamer = (props) =>{
    return (
    <div>
        <div>
            <label>{props.caption}</label>
        </div>
        <div>
            <Texto longitud={props.longitud} ></Texto>
        </div>
        <div>
            <Boton des_boton={props .des_boton}></Boton>
        </div>
    </div>
    )
}

const Texto = (props) =>{

    return (<input type="text" id="letra_a_evaluar"/>

    )
}
const Etiqueta = (props) =>{

    return (<label>{props.caption}
        </label>
    )
}
function validaLetra(){
    let x = document.getElementById("letra_a_evaluar").value;
    let y = document.getElementById("palabra_id").textContent;
    console.log(y);
    console.log(x);
    if (y.indexOf(x)>-1){
        alert("existe");
    }else{
        document.getElementById("id_imagen").src = cabeza; 
    }
}
const Boton = (props) => {
    return (<button  onClick={validaLetra}   >{props.des_boton}
        </button>
    )
}
export default Gamer;
