//import logo from './logo.svg';
import {useState} from 'react';
import Palabras from "./components/Palabras";
import './App.css';
const source = 'https://palabras-aleatorias-public-api.herokuapp.com/random'
let c = 0;
let splited_word = [];
export default function Home(){
  const [palabra, setPalabra] = useState('');
  const palabras = fetch(source).then(response =>{
    if(response.status === 200){
      if (palabra !== null && c === 0){
          console.log("everything went well");
          response.json().then(data => {
            setPalabra(data.body.Word);
            console.debug(palabra);
            splited_word = palabra.split("");

            console.debug()
            c = 1;
            console.debug(c);
            console.debug(splited_word[0]);
      });
    }
    }
    console.log(response);
  }).catch(error => {
    console.error(error);
  });
  if (palabra === null){

    return <div>No data have been received</div>
  }
  const readWords = () =>{
    console.debug("descargando palabra aleatoria...");
  }
  const registros = [
      {
          charadaWord: 'Perro'
      },
      {
          charadaWord: 'Gato'
      },
      {
          charadaWord: 'Perico'
      },
      {
          charadaWord: 'Tortuga'
      },
      {
          charadaWord: 'Aguila'
      }
  ]

  return(

      <div>
        <div>
          <button className = "button is-primary" onclick = {readWords()} >nueva palabra</button>
        </div>
        <div>
          {
              registros.map(contacto =>{
                  return(
                      <div>
                          <strong>{contacto.charadaWord}</strong>
                      </div>
                  )
              }

              )
          }
        </div>
        <div><Palabras username={palabra}/>
      </div>

      </div>

  )

  }
