import pierna_i from "../img/5.jpg";
import pierna_d from "../img/6.jpg";

const Palabras = (props) => {
    return (
      <div>
      <div style={imgStyle}>
        <Foto username={props.username} picture={props.image} />
      </div>
      <div>
        {props.username}
      </div>
      </div>
    );
  };
  const imgStyle = {
    border: "1px solid red",
    fontWeigth: "bold",
    width: "280px",
    mraginBottom: "10px"
  }
  const Foto = (props) => {
    const imgSrc = `${base}?text=${props.username}`;
    //console.log(imgSrc);
    return <img src={imgSrc} alt ={props.username} />;
  };

