export default function Home(){
  //const [palabras, setPalabras] = useState([]);
  let palabra;
  const palabras = fetch(source);
  if (palabra === null){
    return <div>No data have been received</div>
  }
  const readWords = () =>{
    console.debug("descargando palabra aleatoria...");
  }
  const registros = [
      {
          charadaWord: 'Perro'
      },
      {
          charadaWord: 'Gato'
      },
      {
          charadaWord: 'Perico'
      },
      {
          charadaWord: 'Tortuga'
      },
      {
          charadaWord: 'Aguila'
      }
  ]

  return(

      <div>
        <div>
          <button className = "button is-primary" onclick = {readWords()} >nueva palabra</button>
        </div>
        <div className = "container">
          <h2>HangMan:</h2>
          {palabras.map((palabra,index)=>{
            return <></>
          })}
        </div>
        <div>
          {
              registros.map(contacto =>{
                  return(
                      <div>
                          <strong>{contacto.charadaWord}</strong>
                      </div>
                  )
              }

              )
          }
        </div>
        <div>omar 
      </div>
      </div>

  )

  }
